const white = '#8000ff';
const gray = '#0a0a0a';
let width = canvas.width / Tilemap[0].length;
let height = canvas.height / Tilemap.length;
let start = document.querySelector("#game-of-life-start");
let resetBtn = document.querySelector("#game-of-life-reset");
let generation = document.querySelector("#game-of-life-generation");
let random = document.querySelector("#game-of-life-random");
let startBool = false;
let resetBool = false;
let nbrGeneration = 0;

//  Start Event Handler
start.addEventListener('click', () => {
    startBool = startBool==true ? false : true;
    if (!startBool) {
        // reset(Tilemap);
        resetBool = !startBool;
    }
});

//  Random Event Handler
random.addEventListener('click', () => {
    randomize(Tilemap);
    nbrGeneration = 0;
});

// Reset Event Handler
resetBtn.addEventListener('click', () => {
    resetBool = true;
    startBool = !resetBool;
    nbrGeneration = 0;
    reset(Tilemap);
    clearInterval(setInterval(() => {}, 500));
});

// Click system
let canvasX = canvas.getClientRects()['0'].x;
let canvasY = canvas.getClientRects()['0'].y;
canvas.addEventListener('mousemove', (e) => {
    for (let i=0; i<Tilemap.length; i++){
        for (let j=0; j<Tilemap[i].length; j++) {
            let x = canvasX + (j*(width));
            let y = canvasY + (i*(height));
            if (e.clientX > x && e.clientX < x+width && e.clientY > y && e.clientY < y+height) {
                ctx.fillStyle = '#CCD6DD';
                ctx.fillRect((j*width), (i*height), width, height);
            }
        }
    }
});

//  Rendering the tilemap for first time
render(Tilemap, width, height, white, gray, ctx);

// canvas.addEventListener('click', (e) => {
//     for (let i=0; i<Tilemap.length; i++){
//         for (let j=0; j<Tilemap[i].length; j++) {
//             let x = canvasX + (j*(width));
//             let y = canvasY + (i*(height));
//             if (e.clientX > x && e.clientX < x+width && e.clientY > y && e.clientY < y+height) {
//                 Tilemap[i][j] = (Tilemap[i][j] == 1 ? 0 : 1);
//             }
//         }
//     }
// });

//  Main loop of the game with interval of 250 milliseconds
setInterval(() => {
    if (startBool) {
        start.value = 'Ajanona';
        render(NextGen, width, height, white, gray, ctx);
        check(Tilemap, Neighbour, width, height);
        NextGen = update(Tilemap, Neighbour);
        generation.innerHTML = `Taranaka faha: ${nbrGeneration++}`;
    }
    else if (!startBool) {
        start.value = 'Mandefa';
        clearInterval(setInterval(() => {}, 500));
        render(Tilemap, width, height, gray, white, ctx);
        generation.innerHTML = `Taranaka faha : ${nbrGeneration}`;
    }
}, 1000 / 10);